export default {
  name: "CommentForm",
  props: {
    newReplyContent: {
      type: String,
      required: true
    },
    addComment: {
      type: Function,
      required: true
    }
  },
  data() {
    return {
      replyContent: this.newReplyContent
    };
  },
  watch: {
    newReplyContent(newVal) {
      this.replyContent = newVal;
    }
  },
  methods: {
    handleSubmit() {
      this.addComment(this.newReplyContent);
      newReplyContent = "";
    }
  }
};
