export const isAuthor = (currentUser, author) => currentUser === author

export const saveComments = (propBook, Comment) => {
  const data = {
    ...propBook,
    comments: Comment
  }
  const jsonData = JSON.stringify(data)
  localStorage.setItem("comments", jsonData)
}
export const addComment = (bookId, Comment, newComment) => {
  const data = {
    bookId: bookId,
    id: Comment.length + 1,
    author: "Bảo",
    content: newComment,
    replies: []
  }
  if (bookId) {
    Comment.push(data)
    saveComments()
  }

  newComment = ""
}
