export const isAuthor = (currentUser, author) => currentUser === author

export const addComment = (bookId, Comment, newComment, propsId) => {
  const data = {
    bookId: bookId,
    id: Comment.length + 1,
    author: "Bảo",
    content: newComment,
    replies: []
  }
  if (propsId) {
    Comment.push(data)
  }

  this.newComment = ""
}
