export const isAuthor = (currentUser, author) => currentUser === author

export const addComment = (bookId) => {
  const data = {
    bookId: bookId,
    id: this.comments.length + 1,
    author: "Bảo",
    content: this.newComment,
    replies: []
  }
  if (this.book.id) {
    this.comments.push(data)
    this.saveComments()
  }

  this.newComment = ""
}
