export const isAuthor = (currentUser, author) => currentUser === author

export const saveComments = (propBook, Comment) => {
  const data = {
    ...propBook,
    comments: Comment
  }
  const jsonData = JSON.stringify(data)
  localStorage.setItem("comments", jsonData)
}

export const addComment = (book, Comment, newComment) => {
  const data = {
    bookId: book.id,
    id: Comment.length + 1,
    author: "Bảo",
    content: newComment,
    replies: []
  }

  if (book.id) {
    Comment.push(data)
    saveComments(book, Comment)
  }

  newComment = ""
}

export const deleteComment = (commentId) => {
  const index = this.comments.findIndex((comment) => comment.id === commentId)
  this.comments.splice(index, 1)
  this.saveComments()
}
