export const isAuthor = (currentUser, author) => currentUser === author

export const saveComments = (propBook, Comment) => {
  const data = {
    ...propBook,
    comments: Comment
  }
  const jsonData = JSON.stringify(data)
  localStorage.setItem("comments", jsonData)
}

export const addComment = (book, Comment, newComment) => {
  console.log(book)
  const data = {
    bookId: book.id,
    id: Comment.length + 1,
    author: "Bảo",
    content: newComment,
    replies: []
  }
  if (book.id) {
    Comment.push(data)
    saveComments(book, Comment)
  }

  newComment = ""
}
