export const isAuthor = (currentUser, author) => currentUser === author

export const saveComments = (propBook, Comment) => {
  console.log(Comment, "đá")
  const data = {
    ...propBook,
    comments: Comment
  }
  const jsonData = JSON.stringify(data)
  localStorage.setItem("comments", jsonData)
}

export const addComment = (book, Comment, newComment) => {
  const data = {
    bookId: book.id,
    id: Comment.length + 1,
    author: "Bảo",
    content: newComment,
    replies: []
  }

  if (book.id) {
    Comment.push(data)
    saveComments(book, Comment)
    newComment = ""
  }

  return Comment
}

export const deleteComment = (comments, commentId) => {
  const index = comments.findIndex((comment) => comment.id === commentId)
  comments.splice(index, 1)
  saveComments()
}

export const addReply = (params, newReplyContent) => {
  const newCommentOrReply = {
    id: params.id++,
    author: "Bảo",
    content: newReplyContent,
    editing: false,
    replies: []
  }
  params.replies.push(newCommentOrReply)

  saveComments()
  this.replyCommentId = null
  this.newReplyContent = ""
}
